CREATE TABLE `rss2tlg_summarization` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'Первичный ключ',
  `item_id` int(10) unsigned NOT NULL COMMENT 'ID новости (FK -> rss2tlg_items.id)',
  `feed_id` int(10) unsigned NOT NULL COMMENT 'ID источника RSS',
  `status` enum('pending','processing','success','failed','skipped') NOT NULL DEFAULT 'pending' COMMENT 'Статус суммаризации',
  `article_language` varchar(10) DEFAULT NULL COMMENT 'Язык статьи (en, ru, и т.д.)',
  `category_primary` varchar(100) DEFAULT NULL COMMENT 'Основная категория',
  `category_primary_en` varchar(100) DEFAULT NULL COMMENT 'Основная категория на английском',
  `category_secondary` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT 'Массив дополнительных категорий (до 2)' CHECK (json_valid(`category_secondary`)),
  `category_secondary_en` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT 'Массив дополнительных категорий на английском' CHECK (json_valid(`category_secondary_en`)),
  `headline` varchar(500) NOT NULL DEFAULT '' COMMENT 'Заголовок новости',
  `summary` text DEFAULT NULL COMMENT 'Краткое содержание (суммаризация)',
  `keywords` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT 'Массив ключевых слов (до 5)' CHECK (json_valid(`keywords`)),
  `keywords_en` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT 'Массив ключевых слов на английском' CHECK (json_valid(`keywords_en`)),
  `importance_rating` tinyint(3) unsigned DEFAULT NULL COMMENT 'Рейтинг важности (1-20)',
  `dedup_canonical_entities` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT 'Ключевые сущности для дедупликации' CHECK (json_valid(`dedup_canonical_entities`)),
  `dedup_canonical_entities_en` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT 'Ключевые сущности на английском для дедупликации' CHECK (json_valid(`dedup_canonical_entities_en`)),
  `dedup_core_event` text DEFAULT NULL COMMENT 'Описание ключевого события (1-2 предложения)',
  `dedup_core_event_en` text DEFAULT NULL COMMENT 'Описание ключевого события на английском',
  `dedup_numeric_facts` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT 'Числовые факты и даты' CHECK (json_valid(`dedup_numeric_facts`)),
  `model_used` varchar(150) DEFAULT NULL COMMENT 'Модель AI использованная для анализа',
  `tokens_used` int(10) unsigned DEFAULT NULL COMMENT 'Количество использованных токенов',
  `tokens_prompt` int(10) unsigned DEFAULT NULL COMMENT 'Токены промпта',
  `tokens_completion` int(10) unsigned DEFAULT NULL COMMENT 'Токены completion',
  `tokens_cached` int(10) unsigned DEFAULT NULL COMMENT 'Токены из кеша',
  `processing_time_ms` int(10) unsigned DEFAULT NULL COMMENT 'Время обработки в миллисекундах',
  `cache_hit` tinyint(1) NOT NULL DEFAULT 0 COMMENT 'Был ли использован кеш',
  `retry_count` tinyint(3) unsigned NOT NULL DEFAULT 0 COMMENT 'Количество повторов при ошибках',
  `error_message` text DEFAULT NULL COMMENT 'Сообщение об ошибке',
  `error_code` varchar(50) DEFAULT NULL COMMENT 'Код ошибки',
  `processed_at` datetime DEFAULT NULL COMMENT 'Время успешной обработки',
  `created_at` datetime NOT NULL DEFAULT current_timestamp() COMMENT 'Время создания записи',
  `updated_at` datetime NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp() COMMENT 'Время последнего обновления',
  PRIMARY KEY (`id`),
  UNIQUE KEY `idx_item_id` (`item_id`),
  KEY `idx_feed_id` (`feed_id`),
  KEY `idx_status` (`status`),
  KEY `idx_importance_rating` (`importance_rating`),
  KEY `idx_category_primary` (`category_primary`),
  KEY `idx_article_language` (`article_language`),
  KEY `idx_processed_at` (`processed_at`),
  KEY `idx_feed_status` (`feed_id`,`status`),
  KEY `idx_category_primary_en` (`category_primary_en`)
) ENGINE=InnoDB AUTO_INCREMENT=22 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='Результаты AI суммаризации и категоризации новостей';
/*!40101 SET character_set_client = @saved_cs_client */;
INSERT INTO `rss2tlg_summarization` VALUES
(1,927,3,'success','ru','science',NULL,'[\"space\",\"environment\"]',NULL,'На Солнце зафиксирована мощная вспышка класса Х','В понедельник на Солнце произошла мощная вспышка класса Х. Об этом сообщили в Институте прикладной геофизики.  Вспышка была зафиксирована и изучается специалистами.  Данные о последствиях вспышки пока не сообщаются.  Вспышки класса Х являются самыми мощными из известных солнечных вспышек и могут оказывать влияние на геомагнитную обстановку на Земле.','[\"солнечная вспышка\",\"солнце\",\"класс х\",\"геофизика\",\"космическая погода\"]',NULL,8,'[\"Солнце\",\"Институт прикладной геофизики\"]',NULL,'Мощная солнечная вспышка класса Х была зафиксирована на Солнце в понедельник.',NULL,'[\"класс Х\",\"понедельник\"]','google/gemma-3-27b-it',2395,2078,317,0,NULL,0,0,NULL,NULL,'2025-11-10 10:27:55','2025-11-10 10:27:03','2025-11-10 10:27:55'),
(2,928,3,'success','ru','politics',NULL,'[\"transport\"]',NULL,'Лукашенко поручил доставить литовских водителей грузовиков к границе','Президент Белоруссии Александр Лукашенко дал распоряжение обеспечить доставку водителей литовских грузовиков, застрявших на территории Белоруссии, к контрольно-пропускным пунктам на границе. Лукашенко заявил, что водителей не будут обижать, отметив, что люди мучились в сложившейся ситуации. Инцидент связан с задержанием грузового транспорта из Литвы на белорусской территории. Президент лично вмешался в ситуацию для разрешения конфликта.','[\"лукашенко\",\"литовские грузовики\",\"белоруссия\",\"граница\",\"водители\"]',NULL,8,'[\"Александр Лукашенко\",\"Белоруссия\",\"Литва\"]',NULL,'Президент Белоруссии Александр Лукашенко дал поручение доставить водителей застрявших литовских грузовиков к контрольно-пропускным пунктам на границе, пообещав, что их не будут обижать',NULL,'[]','deepseek/deepseek-v3.2-exp',2433,2053,380,0,NULL,0,0,NULL,NULL,'2025-11-10 10:28:43','2025-11-10 10:27:55','2025-11-10 10:28:43'),
(3,929,3,'success','ru','war',NULL,'[\"military\"]',NULL,'ВС РФ взяли под контроль 244 здания в Красноармейске за сутки','Российские вооруженные силы за последние сутки установили контроль над 244 зданиями в Красноармейске (Покровске). Штурмовые группы ВС РФ ведут активные наступательные действия в микрорайоне Динас, северо-западных и восточных кварталах Центрального района города. Также проводится зачистка территории западной промзоны. Информация о боевых действиях подтверждена Министерством обороны Российской Федерации. Город Красноармейск расположен в Донецкой Народной Республике.','[\"вс рф\",\"красноармейск\",\"покровск\",\"днр\",\"наступательные действия\"]',NULL,14,'[\"ВС РФ\",\"Минобороны РФ\",\"Красноармейск\",\"Донецкая Народная Республика\"]',NULL,'Российские штурмовые группы за сутки взяли под контроль 244 здания в Красноармейске (Покровске) и продолжают наступательные действия в микрорайоне Динас и Центральном районе города',NULL,'[\"244 здания\",\"1 сутки\"]','deepseek/deepseek-v3.2-exp',2492,2099,393,0,NULL,0,0,NULL,NULL,'2025-11-10 10:29:34','2025-11-10 10:28:44','2025-11-10 10:29:34'),
(4,952,5,'success','en','space',NULL,'[\"technology\"]',NULL,'Blue Origin delays New Glenn launch to November 12','Blue Origin postponed its second launch attempt of the New Glenn rocket. The launch, crucial for Jeff Bezos\' company, aims to demonstrate the reusability of its rockets and deliver the first commercial payloads to orbit. The company will now target November 12th for the launch. This launch is a key milestone for Blue Origin as it competes in the growing space launch market.','[\"blue origin\",\"new glenn\",\"space launch\",\"jeff bezos\",\"rocket reusability\"]',NULL,8,'[\"Blue Origin\",\"Jeff Bezos\",\"New Glenn\"]',NULL,'Blue Origin delayed the second launch attempt of its New Glenn rocket, rescheduling for November 12th.',NULL,'[\"November 12\"]','google/gemma-3-27b-it',2351,2077,274,0,NULL,0,0,NULL,NULL,'2025-11-10 10:30:43','2025-11-10 10:29:34','2025-11-10 10:30:43'),
(5,953,5,'success','en','business',NULL,'[\"education\",\"social\"]',NULL,'Slow Ventures hosts etiquette finishing school for startup founders','Slow Ventures organized a three-hour \'Etiquette Finishing School\' event this week to help startup founders develop professional skills. The program covered essential business etiquette topics including how to execute the perfect handshake, improve public speaking abilities, and understand proper office decorum. The venture capital firm aims to equip entrepreneurs with social and professional skills beyond just business acumen. The intensive session focused on practical skills that founders can immediately apply in business settings. This initiative reflects the growing recognition that interpersonal skills are crucial for entrepreneurial success. The finishing school approach represents an unconventional but valuable addition to traditional startup support services.','[\"slow ventures\",\"etiquette\",\"founders\",\"business skills\",\"professional development\"]',NULL,6,'[\"Slow Ventures\"]',NULL,'Slow Ventures hosted a three-hour \'Etiquette Finishing School\' event teaching startup founders professional etiquette skills including handshakes, public speaking, and office decorum',NULL,'[\"3 hours\"]','deepseek/deepseek-v3.2-exp',2296,1998,298,0,NULL,0,0,NULL,NULL,'2025-11-10 10:31:38','2025-11-10 10:30:44','2025-11-10 10:31:38'),
(6,954,5,'success','en','entertainment',NULL,'[\"business\",\"technology\"]',NULL,'YouTube TV offers $20 credit after Disney channel blackout','YouTube TV is providing a $20 credit to its subscribers due to a prolonged blackout of Disney-owned channels, including ESPN and ABC. The outage, lasting over a week, has caused dissatisfaction among users. The credit will be applied to customers’ next billing statement as compensation for the disruption. This move aims to retain subscribers affected by the lack of access to popular Disney content. The dispute between YouTube TV and Disney remains unresolved.','[\"youtube tv\",\"disney\",\"blackout\",\"espn\",\"abc\"]',NULL,7,'[\"YouTube TV\",\"Disney\",\"ESPN\"]',NULL,'YouTube TV announced a $20 credit for subscribers affected by a week-long blackout of Disney-owned channels like ESPN and ABC.',NULL,'[\"$20\",\"1 week\"]','google/gemma-3-27b-it',2370,2080,290,0,NULL,0,0,NULL,NULL,'2025-11-10 10:32:30','2025-11-10 10:31:39','2025-11-10 10:32:30'),
(7,930,3,'success','ru','politics','politics','[\"diplomacy\"]','[\"diplomacy\"]','Лукашенко сообщил о готовящихся переговорах Белоруссии с США','Президент Белоруссии Александр Лукашенко объявил о предстоящих переговорах официального Минска с делегацией США. Заявление было сделано в понедельник на совещании с правительством страны. Переговоры запланированы на ближайшее время, что свидетельствует о возможной нормализации или развитии диалога между Белоруссией и Соединенными Штатами. Это событие представляет значительный интерес в контексте международных отношений и региональной политики.','[\"лукашенко\",\"белоруссия\",\"сша\",\"переговоры\",\"дипломатия\"]','[\"lukashenko\",\"belarus\",\"usa\",\"negotiations\",\"diplomacy\"]',13,'[\"Александр Лукашенко\",\"Белоруссия\",\"США\",\"Минск\"]','[\"Alexander Lukashenko\",\"Belarus\",\"USA\",\"Minsk\"]','Президент Белоруссии Александр Лукашенко объявил о предстоящих переговорах официального Минска с делегацией США в ближайшее время','Belarus President Alexander Lukashenko announced upcoming negotiations between official Minsk and a US delegation in the near future','[\"понедельник\"]','deepseek/deepseek-v3.2-exp',3629,3170,459,0,NULL,0,0,NULL,NULL,'2025-11-10 21:43:33','2025-11-10 21:43:19','2025-11-10 21:43:33'),
(8,931,3,'success','ru','war','war','[\"military\"]','[\"military\"]','Минобороны РФ: за сутки сбито 124 беспилотника и 6 снарядов HIMARS','Министерство обороны России сообщило о значительных успехах противовоздушной обороны за последние сутки. По данным ведомства, силы ПВО уничтожили 124 беспилотных летательных аппарата самолетного типа. Также были сбиты шесть реактивных снарядов системы залпового огня HIMARS американского производства. Черноморский флот в северо-восточной части Черного моря уничтожил четыре безэкипажных катера Вооруженных сил Украины. Эти данные отражают продолжающуюся интенсивность боевых действий в воздушном пространстве и акватории Черного моря.','[\"минобороны рф\",\"беспилотники\",\"силы пво\",\"черноморский флот\",\"himars\"]','[\"russian defense ministry\",\"drones\",\"air defense forces\",\"black sea fleet\",\"himars\"]',13,'[\"Минобороны России\",\"Силы ПВО\",\"Черноморский флот\",\"HIMARS\",\"ВСУ\"]','[\"Russian Defense Ministry\",\"Air Defense Forces\",\"Black Sea Fleet\",\"HIMARS\",\"Ukrainian Armed Forces\"]','Министерство обороны России заявило об уничтожении за сутки 124 беспилотников самолетного типа, 6 снарядов HIMARS и 4 безэкипажных катера ВСУ','The Russian Defense Ministry reported the destruction of 124 aircraft-type drones, 6 HIMARS rockets, and 4 Ukrainian unmanned boats over the past 24 hours','[\"124\",\"6\",\"4\",\"24 hours\"]','deepseek/deepseek-v3.2-exp',3789,3248,541,0,NULL,0,0,NULL,NULL,'2025-11-10 21:43:52','2025-11-10 21:43:33','2025-11-10 21:43:52'),
(9,932,3,'success','ru','military','military','[\"politics\",\"war\"]','[\"politics\",\"war\"]','Саперы из КНДР начнут работы в Курской области при благоприятных условиях','Заместитель главы Министерства иностранных дел России Андрей Руденко заявил, что саперы из Северной Кореи начнут работы в Курской области, как только для этого будут созданы благоприятные условия. Чиновник сделал это заявление журналистам, отвечая на соответствующий вопрос. Руденко подчеркнул, что работы начнутся сразу после создания подходящей ситуации. Это подтверждает планы по привлечению северокорейских специалистов для разминирования в российском регионе. Заявление было сделано в контексте продолжающихся военных действий в приграничных районах.','[\"саперы\",\"кндр\",\"курская область\",\"андрей руденко\",\"разминирование\"]','[\"sappers\",\"north korea\",\"kursk region\",\"andrey rudenko\",\"demining\"]',11,'[\"Андрей Руденко\",\"МИД России\",\"КНДР\",\"Курская область\"]','[\"Andrey Rudenko\",\"Russian Foreign Ministry\",\"North Korea\",\"Kursk Region\"]','Заместитель министра иностранных дел России Андрей Руденко заявил, что саперы из Северной Кореи начнут работы по разминированию в Курской области, как только будут созданы благоприятные условия','Russian Deputy Foreign Minister Andrey Rudenko announced that North Korean sappers will begin demining operations in the Kursk Region as soon as favorable conditions are established','[]','deepseek/deepseek-v3.2-exp',3717,3199,518,0,NULL,0,0,NULL,NULL,'2025-11-10 21:44:08','2025-11-10 21:43:52','2025-11-10 21:44:08'),
