CREATE TABLE `rss2tlg_items` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'Первичный ключ',
  `feed_id` int(10) unsigned NOT NULL COMMENT 'Идентификатор источника',
  `content_hash` varchar(32) NOT NULL COMMENT 'MD5 хеш контента для дедупликации',
  `guid` varchar(512) DEFAULT NULL COMMENT 'GUID элемента из RSS',
  `title` varchar(512) NOT NULL COMMENT 'Заголовок новости',
  `link` varchar(1024) NOT NULL COMMENT 'Ссылка на новость',
  `description` text DEFAULT NULL COMMENT 'Краткое описание',
  `content` mediumtext DEFAULT NULL COMMENT 'Полный контент',
  `pub_date` datetime DEFAULT NULL COMMENT 'Дата публикации в источнике',
  `author` varchar(255) DEFAULT NULL COMMENT 'Автор',
  `categories` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT 'Категории (массив)' CHECK (json_valid(`categories`)),
  `enclosures` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT 'Вложения: изображения, аудио, видео' CHECK (json_valid(`enclosures`)),
  `extracted_content` mediumtext DEFAULT NULL COMMENT 'Текст статьи извлеченный с веб-страницы',
  `extracted_images` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT 'Массив изображений из статьи' CHECK (json_valid(`extracted_images`)),
  `extracted_metadata` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT 'Мета-данные страницы' CHECK (json_valid(`extracted_metadata`)),
  `extraction_status` enum('pending','success','failed','skipped') NOT NULL DEFAULT 'pending' COMMENT 'Статус извлечения контента',
  `extraction_error` text DEFAULT NULL COMMENT 'Сообщение об ошибке при извлечении',
  `extracted_at` datetime DEFAULT NULL COMMENT 'Дата и время извлечения контента',
  `is_published` tinyint(1) NOT NULL DEFAULT 0 COMMENT 'Флаг публикации в Telegram',
  `created_at` datetime NOT NULL DEFAULT current_timestamp() COMMENT 'Время создания записи',
  `updated_at` datetime NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp() COMMENT 'Время последнего обновления',
  PRIMARY KEY (`id`),
  UNIQUE KEY `idx_content_hash` (`content_hash`),
  KEY `idx_feed_id` (`feed_id`),
  KEY `idx_is_published` (`is_published`),
  KEY `idx_pub_date` (`pub_date`),
  KEY `idx_feed_published` (`feed_id`,`is_published`),
  KEY `idx_extraction_status` (`extraction_status`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='Новости из RSS/Atom источников с извлеченным контентом';
